from pathlib import Path


class CompilerResult:
    def __init__(self) -> None:
        pass
    def get_path(self) -> Path:
        """Get path to compiled source file."""
    def get_return_code(self) -> int:
        """Get glslang return code."""
    def good() -> bool:
        """Check if compilation didn't fail."""
    def get_compiled_code(self) -> bytes:
        """Get compiled code generated by glslang."""